def generate_paths(depth, x=0, y=0):
    if x == depth:
        yield ((x, y),)
    else:
        for path in generate_paths(depth, x+1, y):
            yield ((x, y),) + path
        for path in generate_paths(depth, x+1, y+1):
            yield ((x, y),) + path

