    def __repr__(self):
        return repr(self._list)

    def __getitem__(self, item):
        print("Accessed element {}".format(item))
        return self._list[item]

    def __setitem__(self, key, value):
        print("Set element {} to {}".format(key, value))
        self._list[key] = value

